# Problem link: https://rosalind.info/problems/dna/
# Counting DNA Nucleotides

def get_nucleotide_amounts(dna):
    amounts = {"A": 0, "C": 0, "G": 0, "T": 0}
    for nuc in dna:
        amounts[nuc] += 1
    print(amounts["A"], amounts["C"], amounts["G"], amounts["T"])

dna_seq = ""

get_nucleotide_amounts(dna_seq)